namespace http {

daemon::daemon(boost::asio::io_service& io,
               const std::string& service,
               const callback_type& callback)
    : m_io(io), m_acceptor(io), m_resolver(io), m_callback(callback) {
  tcp::resolver::query query("0.0.0.0", service);
  m_resolver.async_resolve(query,
                           boost::bind(&daemon::on_resolve,
                                       boost::ref(*this),
                                       _1,
                                       _2));

void daemon::on_resolve(const boost::system::error_code& e,
                        tcp::resolver::iterator& iter) {
  if (e) {
    return;
  }

  m_acceptor.open(m_acceptor.local_endpoint().protocol());
  m_acceptor.bind(*iter);
  m_acceptor.listen();
  tcp::socket* socket(new tcp::socket(m_io));
  m_acceptor.async_accept(*socket,
                          boost::bind(&daemon::on_accept,
                                      boost::ref(*this),
                                      _1,
                                      socket));
}

void daemon::on_accept(const boost::system::error_code& e,
                       boost::asio::ip::tcp::socket* socket) {
  if (e) {
    delete socket;
    return; //throw boost::system::system_error(e);
  }

  handle_request(socket);

